import { ScrollingModule } from '@angular/cdk/scrolling';
import { CommonModule } from '@angular/common';
import { Component, DestroyRef, inject, OnInit } from '@angular/core';
import { NzCardModule } from 'ng-zorro-antd/card';
import { NzGridModule } from 'ng-zorro-antd/grid';
import { NzImageModule, NzImageService } from 'ng-zorro-antd/image';
import { NzSkeletonModule } from 'ng-zorro-antd/skeleton';
import { Observable } from 'rxjs';
import { PageHeaderSectionComponent } from 'src/app/shared/ui-common/page-header-section/page-header-section';
import { GallaryService } from './data/gallary.service';
import { LazyImgDirective } from 'src/app/shared/util-common/directives/lazyImage/lazyImage.directive';

@Component({
  selector: 'app-gallary',
  standalone: true,
  imports: [
    CommonModule,
    NzGridModule,
    NzImageModule,
    NzSkeletonModule,
    NzCardModule,
    ScrollingModule,
    PageHeaderSectionComponent,
    LazyImgDirective,
  ],
  templateUrl: './gallary.component.html',
  styleUrl: './gallary.component.scss',
})
export class GallaryComponent implements OnInit {
  // props
  description = `Our collection of memories captured through the lens, showcasing the vibrant moments and cherished experiences that define our journey. Explore the visual stories that highlight the essence of our community and the spirit of togetherness.`;
  fallback =
    'data:image/png;base64,';

  gallary$: Observable<any> | undefined;
  gallary: any;
  private galleryService = inject(GallaryService);

  private destroy$ = inject(DestroyRef);
  private nzImageService = inject(NzImageService);

  ngOnInit() {
    this.fetchContents();
  }

  private fetchContents() {
    // this.gallary$ = this.galleryService.getGallaryContents();
    this.galleryService.getGallaryContents().subscribe((res) => {
      this.gallary = res.imageList;
    });
  }

  onClick(): void {
    this.nzImageService.preview(
      this.gallary,
      { nzZoom: 1, nzRotate: 0 }
    );
  }
}
